import React, {useState} from "react";
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link
  } from "react-router-dom";

  import CreateGame from "./CreateGame";
  import JoinGame from "./JoinGame";
  import GameLobby from "./GameLobby";

export default class TopComponent extends React.Component {
    constructor(props){
        super(props)
        this.state = {
            gamestate: null
        }

    }



    gameCreatedCallback = (data) =>{
        this.setState({
            gamestate: data
        })
    }

    gameJoinedCallback = (data) => (
        this.setState({
            gamestate: data
        })
    )

    render(){
        return <div>
            <div>
                The gamestate is: {JSON.stringify(this.state.gamestate)}
            </div>
              <Router>
                    <Switch>
                        <Route path="/create" render={(props) => (
                            <div>
                            <h2>Create a game</h2>
                            <Link to="/" className="main-btn">Main page</Link>
                            <div className="create-game-form">
                            </div>
                            <CreateGame gameCreatedCallback={this.gameCreatedCallback}></CreateGame>
                        </div>
                        )}>

                        </Route>

                        <Route path="/join" render={(props) => (
                            <JoinGame gameJoinedCallback={this.gameJoinedCallback}></JoinGame>
                        )}>
                        </Route>

                        <Route path="/lobby" render={(props) => (
                            <GameLobby gamestate={this.state.gamestate}></GameLobby>
                        )}>
                        </Route>

                        <Route path="/">
                            <Home />
                        </Route>
                    </Switch>
            </Router>
        </div>
    }
}

    function Home() {
        return <div>
                  <h2>Home</h2>
                    <div className="button-list-landing-page">
                      <Link to="/create" className="main-btn">Create Game</Link>
                      <Link to="/join" className="main-btn">Join Game</Link>
                    </div>
              </div>;
      }



    // function Create() {

    //     return <div>
    //               <h2>Create a game</h2>
    //               <Link to="/" className="main-btn">Main page</Link>
    //               <div className="create-game-form">
    //               </div>
    //               <CreateGame ></CreateGame>
    //           </div>;
    //   }

    function Join() {
        return  <div>
                    <h2>Join a game</h2>
                    <Link to="/" className="main-btn">Main page</Link>
                    <JoinGame></JoinGame>
                </div>;
      }

    function Lobby(){
        return <div>
            <GameLobby username="Tobias"></GameLobby>
          </div>

      }
