import React from "react";

export default class GameLobby extends React.Component{
    constructor(props){
        super(props);
        this.state = {
                userID: null,
                username: null
        }
    }

    componentDidMount(){
        if(this.props.gamestate != null){
            this.getUseridFromServer();
        }
    }

    getUseridFromServer = () => {
        console.log("Getting userid from server");
        console.log(this.props.gamestate.gamecode);
        fetch("/get_userid", {
            method: 'POST',
            headers: {
                'Content-Type': 'application/Json'
              },
              body: JSON.stringify({code: this.props.gamestate.gamecode})
          })
        .then((res) => res.json())
        .then((data) => {
            console.log(json.parse(data));
            if(data.userID != null){

                let temp_username = null
                this.props.gamestate.players.forEach(player => {
                    if(player.uID == data.userID){
                        temp_username = player.username;
                    }
                });
                this.setState(
                    {
                        userID: data.userID,
                        username: temp_username
                    });
            }
            else{
                console.log("No userID in data from server...");
            }
        });
    }

    render(){
        if(this.props.gamestate != null){
            return(<div>
                <h1>Welcome to the game lobby, {this.state.username}</h1>
            </div>)
        }
        else{
            return(
                <div>
                    <h1>Welcome to the game lobby, unknown.</h1>
                </div>
            )
        }
    }

}
