import React, {useState} from 'react';
import logo from './logo.svg';
import './App.css';


import CreateGame from './components/CreateGame';
import JoinGame from './components/JoinGame';
import GameLobby from './components/GameLobby';

function App() {

  // Handling the gamestate hook
  const [gamestate, setGamestate] = useState(null);

  return (
    <Router>
  <Switch>
          <Route path="/create">
            <Create />
          </Route>
          <Route path="/join">
            <Join />
          </Route>
          <Route path="/lobby">
            <Lobby />
          </Route>
          <Route path="/">
            <Home />
          </Route>
        </Switch>
  </Router>

  );

  function Home() {
    return <div>
              <h2>Home</h2>
                <div className="button-list-landing-page">
                  <Link to="/create" className="main-btn">Create Game</Link>
                  <Link to="/join" className="main-btn">Join Game</Link>
                </div>
          </div>;
  }



  function Create() {

    return <div>
              <h2>Create a game</h2>
              <Link to="/" className="main-btn">Main page</Link>
              <div className="create-game-form">

              </div>
              <CreateGame onGameCreatedCallback={onGameCreatedCallback} setGamestate={setGamestate}></CreateGame>
          </div>;
  }

  function Join() {
    return  <div>
                <h2>Join a game</h2>
                <Link to="/" className="main-btn">Main page</Link>
                <JoinGame></JoinGame>
            </div>;
  }

  function Lobby(){
    return <div>
        <GameLobby username="Tobias"></GameLobby>
      </div>

  }

  function onGameCreatedCallback(data){
    console.log(JSON.stringify(data))
  }
}



export default App;
