import { json } from "body-parser";
import React from "react";

export default class GameLobby extends React.Component{
    constructor(props){
        super(props);
        this.state = {
                userID: null,
                username: null
        }
    }

    componentDidMount(){
        if(this.props.gamestate != null){
            this.getUseridFromServer();
        }
        else{
            // check if game exists for this userID
            this.getGameFromServerForSessionID();
        }
    }

    getGameFromServerForSessionID = () => {
        console.log("Getting game from server for this sessionID");
        fetch("/get_game_from_session", {
            method: 'GET'
        })
        .then((res) => res.json())
        .then((data) => {

        });
    }

    getUseridFromServer = () => {
        console.log("Getting userid from server");
        console.log(this.props.gamestate.gamecode);
        fetch("/get_userid", {
            method: 'POST',
            headers: {
                'Content-Type': 'application/Json'
              },
              body: JSON.stringify({code: this.props.gamestate.gamecode})
          })
        .then((res) => res.json())
        .then((data) => {
            console.log("data from server: " + data);
            if(data.userID != null){

                let temp_username = null
                this.props.gamestate.players.forEach(player => {
                    if(player.uID == data.userID){
                        temp_username = player.username;
                    }
                });
                this.setState(
                    {
                        userID: data.userID,
                        username: temp_username
                    });
            }
            else{
                console.log("No userID in data from server...");
            }
        });
    }

    Players = () => {
        if(this.gamestate == null){
            return <p>No game created yet.</p>
        }
        else{
            this.state.gamestate.players.forEach(player => {
                return <p>{player.username}</p>
            });
        }
    }

    render(){
        if(this.props.gamestate != null){
            return(<div>
                <h1>Welcome to the game lobby, {this.state.username}</h1>
                <div>
                    <p>Other players</p><br/>
                    {this.Players()}
                </div>
            </div>)
        }
        else{
            return(
                <div>
                    <h1>Welcome to the game lobby, unknown.</h1>
                </div>
            )
        }
    }

}
