import React from "react";
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link,
    Redirect
  } from "react-router-dom";

class CreateGame extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            gamesize: 0,
            players: [],
            username: "",
            gamestate: null,
            redirectToLobby: false
        }
    }


    handleSubmit = (e) => {
        e.preventDefault();
        alert("You clicked submit. Value was:" + this.state.gamesize);
        fetch("/create", {
            method: 'POST',
            headers: {
              'Content-Type': 'application/Json'
            },
            body: JSON.stringify(this.state)
        })
              .then((res) => res.json())
              .then((data) => {
                    console.log(data);
                    let new_gamestate = JSON.parse(data);
                    console.log(new_gamestate.gamesize);
                    console.log(new_gamestate != null);
                    // Want some proper check here if the game was correctly created TODO
                    if(new_gamestate != null){
                        // Redirect to lobby
                        this.setState({gamestate: new_gamestate})
                        this.props.setGamestate(this.state.gamestate);
                        this.setState({redirectToLobby: true})
                    }
                });
          };

    handleGamesizeChange = (e) => {
        this.setState({gamesize: e.target.value})
    }

    handleUsernameChange = (e) => {
        this.setState({username: e.target.value})
    }


    render() {
        if(this.state.redirectToLobby){
            return (
                <div>
                    <Redirect to="/lobby"/>
                </div>
            )
        }
        else{
            return (
                <div>
                    <form onSubmit={this.handleSubmit}>
                        <label>Gamesize
                            <input type="text" value={this.state.gamesize} onChange={this.handleGamesizeChange}></input>
                        </label>
                        <br></br>
                        <label>Username
                            <input type="text" value={this.state.username} onChange={this.handleUsernameChange}></input>
                        </label>
                        <button type="submit">Create game</button>
                    </form>
                </div>
            )
        }
    }
}

export default CreateGame;
