import React from "react";
import { Redirect } from "react-router";

class JoinGame extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            gamecode: 0,
            username: "",
            redirectToLobby: false
        }
    }


    handleSubmit = (e) => {
        e.preventDefault();
        alert("You clicked submit. Value was:" + this.state);
        fetch("/join_game", {
            method: 'POST',
            headers: {
              'Content-Type': 'application/Json'
            },
            body: JSON.stringify(this.state)
        })
              .then((res) => res.json())
              .then((data) => {
                console.log(data);
                    let new_gamestate = data;
                    console.log(new_gamestate.gamesize);
                    console.log(new_gamestate != null);
                    // Want some proper check here if the game was correctly created TODO
                    if(new_gamestate != null){
                        // Redirect to lobby
                        this.setState({gamestate: new_gamestate})
                        console.log("hi")
                        this.props.gameCreatedCallback(this.state.gamestate)
                        this.setState({redirectToLobby: true})
                    }
              });
          };

    handleGameCodeChange = (e) => {
        this.setState({gamecode: e.target.value})
    }

    handleUsernameChange = (e) => {
        this.setState({username: e.target.value})
    }


    render() {
        if(this.state.redirectToLobby){
            return (
                <div>
                    <Redirect to="/lobby"/>
                </div>
            )
        }
        else{
            return (
                <div>
                    <form onSubmit={this.handleSubmit}>
                        <label>Gamecode
                            <input type="text" value={this.state.gamesize} onChange={this.handleGameCodeChange}></input>
                        </label>
                        <br></br>
                        <label>Username
                            <input type="text" value={this.state.username} onChange={this.handleUsernameChange}></input>
                        </label>
                        <button type="submit">Join game</button>
                    </form>
                </div>
            )
        }
    }
}

export default JoinGame;
