import React from "react";
import { Redirect } from "react-router";

function JoinGame (props){

    const [redirectToLobby, setRedirectToLobby] = useState(false)
    const [usernameData, setUsernameData] = useState("")
    const [gameSize, setGameSize] = useState("")


    handleSubmit = (e) => {
        e.preventDefault();
        alert("You clicked submit. Value was:" + this.state);
        fetch("/join_game", {
            method: 'POST',
            headers: {
              'Content-Type': 'application/Json'
            },
            body: JSON.stringify(this.state)
        })
              .then((res) => res.json())
              .then((data) => {
                console.log(data);
                    let new_gamestate = data;
                    console.log(new_gamestate.gamesize);
                    console.log(new_gamestate != null);
                    // Want some proper check here if the game was correctly created TODO
                    if(new_gamestate != null){
                        // Redirect to lobby
                        this.props.setGameState(new_gamestate);
                        console.log("hi")
                        setRedirectToLobby(true)
                    }
              });
          };



        if(redirectToLobby){
            return (
                <div>
                    <Redirect to="/lobby"/>
                </div>
            )
        }
        else{
            return (
                <div>
                    <form onSubmit={this.handleSubmit}>
                        <label>Gamecode
                            <input type="text" value={gameSize} onChange={e => setGameSize(e.target.value)}></input>
                        </label>
                        <br></br>
                        <label>Username
                            <input type="text" value={usernameData} onChange={e => setUsernameData(e.target.value)}></input>
                        </label>
                        <button type="submit">Join game</button>
                    </form>
                </div>
            )
        }
    }


export default JoinGame;
