import React from "react";

class CreateGame extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            gamesize: 0,
            players: [],
        }
    }


    handleSubmit = (e) => {
        e.preventDefault();
        alert("You clicked submit. Value was:" + this.state.gamesize);
        fetch("/create", {
            method: 'POST'
        })
              .then((res) => res.json())
              .then((data) => console.log(data));
          };

    handleChange = (e) => {
        this.setState({gamesize: e.target.value})
    }


    render() {
        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                    <label>Gamesize
                        <input type="text" value={this.state.gamesize} onChange={this.handleChange}></input>
                    </label>
                    <button type="submit">Create game</button>
                </form>
            </div>
        )
    }
}

export default CreateGame;
