import React from "react";

export default class GameLobby extends React.Component{
    constructor(props){
        super(props);
        this.state = {

        }
    }

    getUseridFromServer = () => {
        fetch("/get_userid", {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(this.props.gamestate.gamecode)
        })
        .then((res) => res.json())
        .then((data) => {
            if(data.userID != null){
                this.setState(
                    {
                        userID: data.userID
                    });
            }
            else{
                console.log("No userID in data from server...");
            }
        });
    }

    render(){
        if(this.props.gamestate != null){
            return(<div>
                <h1>Welcome to the game lobby, {JSON.stringify(this.props.gamestate.players)}</h1>
            </div>)
        }
        else{
            return(
                <div>
                    <h1>Welcome to the game lobby, unknown.</h1>
                </div>
            )
        }
    }

}
